import React from 'react';
import {StyleSheet} from 'react-native';
import {Icon, Panel, PanelGroup} from 'rsuite';
import 'rsuite/dist/styles/rsuite-default.css';

export type FaqVProps = {
  faqData?: any[];
  title?:any;
  headingStyle?:any;
};

const faqData = [
  {
    heading:"FAQ"
  },
  {
    Question: <span><Icon icon="user"/>how are you</span>,
    Answer: 'answer',
  },
  {
    Question: 'hey this is FAQ',
    Answer: 'this is answer',
  },
];

const faqV = (props: FaqVProps) => {
  const output: any = [];
  const getFaq = () => {
    Array.isArray(props?.faqData) && props?.faqData.forEach(items => {
        output.push(
        <Panel  header={<span><Icon icon={items.icon}/>{items.Question}</span>}>{items.Answer}</Panel>

        );
      });
    return output;
  };

  return (
    <div>
        <h3 style={StyleSheet.flatten([props?.headingStyle])}>{props?.title?.heading}</h3>
    <PanelGroup accordion bordered >
      {getFaq()}
    </PanelGroup>
    </div>
  );
};

export default faqV;
